// Author : Simone Masiero

import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';

class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2]]
    };
    this.takeSong = this.takeSong.bind(this);
  } 

  takeSong(song) {
    this.setState({
      song
    });
  }

   render() {
    return (
      <div>
        <section className="presentation">
          <div className="loghi">
            <img className="logo" src={require('./../../logo_conservatorio.png')} />
            <img className="logo" src={require('./../../logo_lounge_b.png')} />
          </div>
          <header className="header">
            <h1 style={{color:"#000000"}}>T I A S C O L T O</h1>
            <h3 class="presentationText"> tiascolto is the ear-training application of the <a href="http://www.conservatorio.ch/it/scuola-universitaria">Conservatorio della Svizzera Italiana</a>, University of Music (SUPSI). 
            <br/>
            <br/>
              Born from an idea of the composer Alberto Barberis, supported by Spazio21 and under the didactic supervision of teachers Ivo Antognini and Nadir Vassena, ti ascolto is now available in its first web version, developed by Simone Masiero in collaboration with CodeLounge and Universit√† della Svizzera Italiana (USI).
            <br/> 
            <br/>
              This version is the first web version and the whole project is still in the development phase.  </h3>
          </header>
        </section>

        <section className="app">
        <div className="grid">
          <div className="item">
            <div>
              <Keyboard
                song = {this.state.song}
                setSong = {this.takeSong}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              song = {this.state.song}
              currentNote = {3}/>
            </div>
          </div>
        </div>
        </section>        
      </div>
      );
  }
}

export default Main;
