// Author : Simone Masiero

import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';

class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2]]
    };
    this.takeSong = this.takeSong.bind(this);
  } 

  takeSong(song) {
    this.setState({
      song
    });
  }

   render() {
    return (
      <div>
        <section className="presentation">
          <div className="loghi">
            <img className="logo" src={require('./../../logo_conservatorio.png')} />
            <img className="logo" src={require('./../../logo_lounge_b.png')} />
          </div>
          <header className="header">
            <h1 >T I A S C O L T O
              <h2>ear training web application</h2>
            </h1>

            <h3 class="presentationText"> tiascolto is the ear training application of the <a href="http://www.conservatorio.ch/it/scuola-universitaria">Conservatorio della Svizzera Italiana</a>, University of Music (SUPSI). 
            <br/>
            <br/>
              From an idea of composer < a href="http://www.albertobarberis.it/">Alberto Barberis </a> 
              and under the didactic supervision of teachers < a href="http://www.ivoantognini.com/MUSIC_COMPOSER/home.html">Ivo Antognini </a> 
              and <a href="http://nadirvassena.ch/"> Nadir Vassena</a>, tiascolto is now available in its first web version.
              <br/> 
            <br/>
              Supported by Spazio21, it is developed by Simone Masiero with < a href="https://www.si.usi.ch/">Software Institute | CodeLounge </a> 
              and <a href="https://www.usi.ch/it"> Universit√† della Svizzera Italiana</a> (USI). 
            <br/> 
            <br/>

              <h4> This is the first web version and the project is still in development </h4> 

              </h3>
          </header>
        </section>

        <section className="app">

        <div className="grid">

          <div className="item">
            <div>
              <Keyboard
                song = {this.state.song}
                setSong = {this.takeSong}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              song = {this.state.song}
              currentNote = {3}/>
            </div>
          </div>
        </div>
        </section>        
      </div>
      );
  }
}

export default Main;
